import { SET_ALERT, REMOVE_ALERT } from '../action/type'
const initailState = []

const alerts = (state = initailState, action) => {
    const { type, payload } = action;
    switch (type) {
        case SET_ALERT:
            return [...state,payload];
        case REMOVE_ALERT:
            return state.filter(alert => alert.id !== payload);
        default:
            return state;
    }
}

export default alerts;
