import axios from 'axios';
import setAlert from './aleart';
import setAuthToken from '../utils/isAuthantication';
import Aleart from '../component/layout/Aleart';
import { BUSNOT_FOUND, BUS_FOUND } from './type';

// Search bus
export const searchbus = ({ from,to, date}) => async dispatch=>{
    const config = {
        headers: { "Content-Type": "application/json" }
    }
    const from = from.split(" ")
    const End = to.split(" ")
    console.log(start,End)
    const body = JSON.stringify({ from, to, date });

    console.log(body)
    try {
        const res = await axios.post('http://localhost:1900/api/admins/admin/searchBus', body, config);
        console.log(res)
        dispatch({
            type: BUS_FOUND,
            payload: res.data
        });
        alert("Bus Found")
    } catch (err) {
        const errors = err.response.data.err;
        if (errors) {
            alert("Not Found")
            errors.forEach(error => dispatch(setAlert(error.msg, 'danger')));
        }
        dispatch({
            type: BUSNOT_FOUND
        })
    }
}
