const { CANNOT_CANCEL,
    CANCEL_TICKET,
    TICKETS,
    NO_TICKET,
    BOOKING_FAIL,
    BOOKING_SUCCESS,
} = '../../action/type';

const initialState = {
    Tickets: [],
    loading: true
};

export const Tickets = (state = initialState, action)=>{
    const { type, payload } = action;
    console.log("data from reducer",type, payload)
    switch (type) {
        // case TICKETS:
        case BOOKING_SUCCESS:
            return {
                ...state,
                Tickets: [payload],
                loading: false
            };
        case CANCEL_TICKET:
            return {
                ...state,
                ...payload,
                loading: false
            };
        case NO_TICKET :
        case CANNOT_CANCEL:
        case BOOKING_FAIL:
            return {
                ...state,
                loading: false
            }
        default:
            return state
    }
}

